  namespaced: true,
  state: {
    items: {},
  },
  mutations: {
    setItem(state, {id, item}) {
      state.items[id] = item
    },
  },
  actions: {
    fetchItem({commit}, id) {
      return fetchItem(id)
        .then(item => {
          commit('setItem', {id, item})
        })
    },
  },
}

